$(window).on('load resize', function () {
    $('#menu-icon ').removeClass('close');
    $("#sub-menu").fadeOut("");
});
$(function () {
    $("#header-top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });

    var $menu = $("#menu-icon");
    $menu.click(function () {
        $(this).toggleClass("close");
        $("#sub-menu").fadeToggle("");
    });

    $("#-sub-menu li").mouseout(function () {
        $("#sub-menu").fadeOut();
        $('#menu-icon').removeClass('close');
    });

    $(window).scroll(function () {
        $("#sub-menu").fadeOut();
        $('#menu-icon').removeClass('close');
    });

    $("#menu-icon2").click(function () {
        $("#respo-menu").slideToggle("");
        $("body").toggleClass("overflow");
        $(".overhide").toggleClass("overflow");
        $("#page").toggleClass("hide");
        $("#menu-icon2").toggleClass("move-icon");
        $("#close").toggleClass("close-move");
        $("#menu-icon2").toggle();
    });
    $("#close").click(function () {
        $("#close").toggleClass("close-move");
        $("#menu-icon2").toggleClass("move-icon");
        $("#close").toggle();
    });
    $(".list-slide1").click(function () {
        $("#tog-menu1").slideToggle("");
        $(".togbtn",this).toggleClass("roll");
    });
    $(".list-slide2").click(function () {
        $("#tog-menu2").slideToggle("");
        $(".togbtn",this).toggleClass("roll");
    });
});
