$(window).on('load resize', function () {
    $('#menu-icon ').removeClass('close');
    $("#sub-menu").fadeOut("");
});
$(function () {
    $("#header-top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });

    var $menu = $("#menu-icon");
    $menu.click(function () {
        $(this).toggleClass("close");
        $("#sub-menu").fadeToggle("");
    });

    $("#-sub-menu li").mouseout(function () {
        $("#sub-menu").fadeOut();
        $('#menu-icon').removeClass('close');
    });

    $(window).scroll(function () {
        $("#sub-menu").fadeOut();
        $('#menu-icon').removeClass('close');
    });

    $("#menu-icon2").click(function () {
        $("#respo-menu").slideDown("");
        $("body").addClass("overflow");
        $(".overhide").removeClass("overflow");
        $("#page").addClass("hide");
        $("#menu-icon2").addClass("icon-slide");
        $("#close").addClass("icon-slide");
    });
    
    $("#close").click(function () {
        $(".overhide").addClass("overflow");
        $("body").removeClass("overflow");
        $("#page").removeClass("hide");
        $("#respo-menu").slideUp("");
        $("#menu-icon2").removeClass("icon-slide");
        $("#close").removeClass("icon-slide");
    });
    
    $(".list-slide1").click(function () {
        $("#tog-menu1").slideToggle("");
        $(".togbtn",this).toggleClass("roll");
    });
    $(".list-slide2").click(function () {
        $("#tog-menu2").slideToggle("");
        $(".togbtn",this).toggleClass("roll");
    });
});
