$(window).on('load resize', function () {  
    $('#menu-icon ').removeClass('close');
    $("#sub-menu").fadeOut("");
});
$(function () {    
    $("#header-top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });
    
    var $menu = $("#menu-icon");
    $menu.click(function () {
        $(this).toggleClass("close");
        $("#sub-menu").fadeToggle("");
    });
    $("#sub-menu li").mouseout(function () {
        $("#sub-menu").fadeOut();
        $('#menu-icon').removeClass('close');
        });
    });
